import React, { useState } from "react";
import ArrowDownwardIcon from '@material-ui/icons/ArrowDownward';

const Trabajos = [
{Empresa:'Green Pixel',Cargo:'Desarrollador Front-End jr',Inicio:'03.2017',Fin:'Actualidad',Tarea:'En un principio asistia de forma freelance al desarrollo web, maquetado, UI, Script. Luego me incorpore en relacion de dependencia y empecé a impulsar proyectos con un equipo, utilizando HTML5, CSS3 y JS (Ecma6). En la actualidad los proyectos los realizamos a través de frameworks como React Js. Con librerias como Bootstrap, Sweet Alert, Materials UI y etc. Deploy y hosteo de proyectos por CLI de Vercel y Heroku. Versionado de proyectos mediante Github.'},
{Empresa:'Pro Insurance Solutions Ltd',Cargo:'Abogado consultor',Inicio:'03/20',Fin:'08/20',Tarea:'Coordinador del Area de legales - ART.'},
{Empresa:'Estudio Juridico CEFER',Cargo:'Abogado Jr.',Inicio:'06/16',Fin:'03/20',Tarea:'Abogado Jr. en estudio dedicado al fuero Laboral - Civil - Comercial.'},
{Empresa:'Jean Factory',Cargo:'Ventas',Inicio:'01/12',Fin:'03/20',Tarea:'Vendedor.'},
{Empresa:'Jean Factory',Cargo:'Coordinador',Inicio:'04/05',Fin:'12/11',Tarea:'Coordinador del area de producción.'}
]

const Work =()=>{
const [deploy, setDeploy]= useState(true);
const k = Object.keys(Trabajos[1])
return(
<div className="App">
{Trabajos.map((job,i) => 
<>
<h3><u>{k[0]}</u>: {job[k[0]]} <span><ArrowDownwardIcon/></span></h3>
<div className={deploy===true?'exp1':null}>
<h3><u>{k[1]}</u>: {job[k[1]]}</h3>
<h3>{job[k[3]] !== 'Actualidad'? k[2]+': '+job[k[2]]: job[k[2]]} - {job[k[3]] !== 'Actualidad'? k[3]+': '+job[k[3]]: 'Actualidad'}</h3>
<h3><u>{k[4]}</u>: {job[k[4]]}</h3>
</div></>)}
</div>
)};

export default Work;
