import './Components.scss';
import './ComponentsDark.scss';
import Button from '@material-ui/core/Button';
import emailjs from "emailjs-com";
import{ init } from 'emailjs-com';
import swal from 'sweetalert';
import React, { useState , useRef } from 'react';

init("user_mHCQpAEjoBs6yyEj0np7i");

export default function Contact(props) {
const [emptyN , SetEmptyN] = useState();
const [emptyF , SetEmptyF] = useState();
const [emptyM , SetEmptyM] = useState();
const [emptyS , SetEmptyS] = useState();
const Fname = useRef();
const Fmail = useRef();
const Fmessage = useRef();
const FSubject = useRef();

    function sendEmail(e) {
        let serviceEmail = 'ezeayacar';
        let templateEmail = 'template_jmio0um';
        let userEmail = 'user_mHCQpAEjoBs6yyEj0np7i';

        e.preventDefault();

        emailjs.sendForm(serviceEmail, templateEmail, e.target, userEmail)
        .then((result) => {
            console.log(result.text);
            swal("Listooo!", "Correo enviado!", "success");
        }, (error) => {
            console.log(error.text);
            swal("Error!", "Algo salio mal el intentar enviar el correo. Contacta al administrador!", "error");
        });
        e.target.reset()
    }

    return(
        <div className={props.darkC==='dark'?'AppD':"App"}>
            <div className="contact">
            <form onSubmit={sendEmail}>
            <input type="text" ref={Fname} placeholder="Full Name" name="name"/>
            <input type="email" ref={Fmail} placeholder="Email Address" name="email"/>
            <input type="text" ref={FSubject} placeholder="Subject" name="subject"/>
            <textarea cols="30" ref={Fmessage} rows="8" placeholder="Your message" name="message"></textarea>
            <Button variant="contained" color="primary" type="submit">Send</Button>
     </form>
            </div>
        </div>
    )
}
