import './Components.scss';
import React, { useState, useEffect } from "react";
import AOS from 'aos';
import 'aos/dist/aos.css';
import Deploy from './Icons/deploy.svg'
import Deploy2 from './Icons/deployDark.svg'
import CloseIcon from '@material-ui/icons/Close';

const Trabajos = [
{Empresa:'Green Pixel',Cargo:'Desarrollador Front-End jr',Inicio:'03/17',Fin:'Actualidad',Tarea:'En un principio asistia de forma freelance al desarrollo web, maquetado, UI, Script. Luego me incorpore en relacion de dependencia y empecé a impulsar proyectos con un equipo, utilizando HTML5, CSS3 y JS (Ecma6). En la actualidad los proyectos los realizamos a través de frameworks como React Js. Con librerias como Bootstrap, Sweet Alert, Materials UI y etc. Deploy y hosteo de proyectos por CLI de Vercel y Heroku. Versionado de proyectos mediante Github.'},
{Empresa:'Pro Insurance Solutions Ltd',Cargo:'Abogado consultor',Inicio:'03/20',Fin:'08/20',Tarea:'Coordinador del Area de legales - ART.'},
{Empresa:'Estudio Juridico CEFER',Cargo:'Abogado Jr.',Inicio:'06/16',Fin:'03/20',Tarea:'Abogado Jr. en estudio dedicado al fuero Laboral - Civil - Comercial.'},
{Empresa:'Jean Factory',Cargo:'Ventas',Inicio:'01/12',Fin:'03/20',Tarea:'Vendedor.'},
{Empresa:'Jean Factory',Cargo:'Coordinador',Inicio:'04/05',Fin:'12/11',Tarea:'Coordinador del area de producción.'}
];
const Works = [
    {Company:'Green Pixel', Position:'Desarrollador Front-End jr',Start:'03/17',End:'Actualidad',Tarea:'En un principio asistia de forma freelance al desarrollo web, maquetado, UI, Script. Luego me incorpore en relacion de dependencia y empecé a impulsar proyectos con un equipo, utilizando HTML5, CSS3 y JS (Ecma6). En la actualidad los proyectos los realizamos a través de frameworks como React Js. Con librerias como Bootstrap, Sweet Alert, Materials UI y etc. Deploy y hosteo de proyectos por CLI de Vercel y Heroku. Versionado de proyectos mediante Github.'},
    {Company:'Pro Insurance Solutions Ltd',Position:'Abogado consultor',Start:'03/20',End:'08/20',Tarea:'Coordinador del Area de legales - ART.'},
    {Company:'Estudio Juridico CEFER',Position:'Abogado Jr.',Start:'06/16',End:'03/20',Tarea:'Abogado Jr. en estudio dedicado al fuero Laboral - Civil - Comercial.'},
    {Company:'Jean Factory',Position:'Ventas',Start:'01/12',Fin:'03/20',Tarea:'Vendedor.'},
    {Company:'Jean Factory',Position:'Coordinador',Start:'04/05',Fin:'12/11',Tarea:'Coordinador del area de producción.'}
    ];

const Work =(props)=>{
const [deploy, setDeploy]= useState();

const k = Object.keys(Trabajos[1])
const Deploys = (e)=>{
setDeploy(e)}

useEffect(() => {
    AOS.init({
    delay: 100,
    duration: 1800,
    once: false, 
    });
    AOS.refresh();
    });

return(
<div className={props.darkW==='dark'?'AppD':"App"}>
<div data-aos="zoom-in-right">
{props.Lang!=='SP'?
<div>
{Trabajos.map((job,i) => 
<div key={i}>
<h2><u>{k[0]}</u>: {job[k[0]]} <span>{i===deploy?<CloseIcon onClick={() => Deploys(-1)} />:<img onClick={() => Deploys(i)}  style={{width:'35px'}} src={props.darkW==='dark'?Deploy2:Deploy} alt="Deploy"/>}</span></h2>
{i===deploy?
<div className='exp'>
<h3><u>{k[1]}</u>: {job[k[1]]}</h3>
<h3>{job[k[3]] !== 'Actualidad'? k[2]+': '+job[k[2]]: job[k[2]]} - {job[k[3]] !== 'Actualidad'? k[3]+': '+job[k[3]]: 'Actualidad'}</h3>
<h3><u>{k[4]}</u>: {job[k[4]]}</h3>
</div>:
<div>
{Trabajos.map((job,i) => 
<div key={i}>
<h2><u>{k[0]}</u>: {job[k[0]]} <span>{i===deploy?<CloseIcon onClick={() => Deploys(-1)} />:<img onClick={() => Deploys(i)}  style={{width:'35px'}} src={props.darkW==='dark'?Deploy2:Deploy} alt="Deploy"/>}</span></h2>
{i===deploy?
<div className='exp'>
<h3><u>{k[1]}</u>: {job[k[1]]}</h3>
<h3>{job[k[3]] !== 'Actualidad'? k[2]+': '+job[k[2]]: job[k[2]]} - {job[k[3]] !== 'Actualidad'? k[3]+': '+job[k[3]]: 'Actualidad'}</h3>
<h3><u>{k[4]}</u>: {job[k[4]]}</h3>
</div> :null}
</div>)}
</div>    
}
</div>)}
</div>:null
}
</div>
<div style={{width:'30%', height:'40px'}}></div>
</div>
)};

export default Work;
