import './App.scss';
import './AppsDark.scss';
import FotoCv from './FotoCV.png';
import {BrowserRouter as Router,Switch,Route,Link,} from "react-router-dom";
import Home from './Home'
import Footer from './Footer'
import Information from './components/Information'
import Resume from './components/Resume'
import Work from './components/Work'
import Skills from './components/Skills'
import Educaction from './components/Education'
import Contact from './components/Contact'
import React, { useState } from 'react';
import {grey} from '@material-ui/core/colors';
import Brightness7Icon from '@material-ui/icons/Brightness7';
import Brightness4Icon from '@material-ui/icons/Brightness4';
import HomeIcon from '@material-ui/icons/Home';
import LanguageIcon from '@material-ui/icons/Language';


const Header =(props)=>(<header id={props.dark==='dark'? 'headerD':null}><h1>{props.title}</h1></header>);

const Nav =()=>{
const [dark, setDark] = useState(true);
const [lang, SetLang] = useState('SP');

const Dmode = ()=>{
if (dark === false){setDark(true)}
else{setDark(false)}
};

const Lan = (event)=>{SetLang(event.target.value)}

console.log(lang)

return(
<Router>
<div id={dark===true?'bar-nav':'bar-navD'}>
<div className='switch'>
<LanguageIcon style={dark===true?null:{color: grey[100]}}/>
<select onChange={Lan} style={{border:'none',backgroundColor:"rgb(3, 172, 155)"}} id='select'>
<option value="SP" selected>SPANISH</option>
<option value="EN">ENGLISH</option>
</select>
</div>

<span className='switch'><Link to={'/'}><HomeIcon style={dark===true?{color: grey[900]}:{color: grey[100]}}/></Link></span>
<span className='switch' onClick={Dmode}>{dark===true?<Brightness4Icon style={{ color: grey[800] }}/>:<Brightness7Icon style={{ color: grey[100] }}/>}</span>
<span className='switch'></span>
</div>
<div id={dark===true?'contain':'containD'}>
<nav>
<div id='first'>
<div id="Foto">
<img src={FotoCv} alt="Foto CV"/>
</div>
<h1>Ezequiel Yacar</h1>
</div>
<div id='nav'>
<ul>
<Link to={'/Information'}><li>{lang==='SP'?'Información personal':'Personal info'}</li></Link>
<Link to={'/Resume'}><li>Resumen Ejecutivo </li></Link>
<Link to={'/Education'}><li>Educación</li></Link>
<Link to={'/Job'}><li>Experiencia Laboral</li></Link>
<Link to={'/Skills'}><li>Aptitudes</li></Link>
<Link to={'/Contact'}><li>Contacto</li></Link>
</ul>
</div>
</nav>
</div>
<Switch>    
<Route exact path='/'><Home/></Route>  
<Route path='/Information'>
<Header title='Información Personal' dark={dark===true?null:'dark'} />
<Information name='Nombre completo' Bday='Fecha de Nacimiento' Bplace='Lugar de Nacimiento' status='Estado Civil' Id='DNI' darkI={dark===true?null:'dark'}/>
</Route>
<Route path='/Resume'><Header dark={dark===true?null:'dark'} title='Resumen Ejecutivo'/><Resume darkR={dark===true?null:'dark'} /></Route>
<Route path='/Education'><Header dark={dark===true?null:'dark'} title='Educación'/><Educaction darkE={dark===true?null:'dark'} /></Route>
<Route path='/Job'><Header dark={dark===true?null:'dark'} title='Experiencia Laboral'/><Work darkW={dark===true?null:'dark'}/></Route>
<Route path='/Skills'><Header dark={dark===true?null:'dark'} title='Aptitudes'/><Skills darkS={dark===true?null:'dark'}/></Route>
<Route path='/Contact'><Header dark={dark===true?null:'dark'} title='Contactate conmigo'/><Contact darkC={dark===true?null:'dark'}/></Route>   
</Switch>
<Footer darkF={dark===true?null:'footerD'}/>
</Router>
)};

export default Nav;
