import React  from 'react';
// Importamos el useFetch que se encargarÃ¡ de manejar el pedido al JSON online
import useFetch from "./useFetch";
import { Switch,Route,Link,NavLink,useParams } from 'react-router-dom'

const Usuario = () => {
  let params = useParams();
  // la constante user se queda con un json que tiene la info del usuario que recibe como parasms.id
  const user = useFetch("https://jsonplaceholder.typicode.com/users/"+params.id);
      if(user.address !== undefined){
          var ciudad = `Ciudad: ${user.address.city}`;
          var empresa = `Empresa: ${user.company.name}`;
      }

      return (  
      <section>
        <h1>{ params.id } - {user.name}</h1>
        <p>Email: {user.email}</p>
        <p>{ciudad}</p>
        <p>{empresa}</p>
        <p><Link to="/">Regresar a la home</Link></p>
      </section>

  )
}

const Usuarios = () => {
    // la constante users recibe la info de todos los usuarios
  const users = useFetch("https://jsonplaceholder.typicode.com/users");
    return (  
        <ul>
          {users.map(user => <li key={user.id}><NavLink to={"/usuario/"+user.id}>{user.name} ({user.address.city})</NavLink></li>)}        
        </ul>      
    )
  }

  const Education = () => (
        <div className="App">
          <header className="App-header">          
            <h1 className="App-title">Titulo del Mundo</h1>
          </header>
          <Switch>
            <Route exact path="/"><Usuarios/></Route>
            <Route exact path="/usuario/:id"><Usuario/></Route>
          </Switch>        
        </div>
      );
export default Education;
